contracts/CarbonCreditToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

/**
 * @title CarbonCreditToken
 * @dev ERC20 token for fungible carbon credits with batch tracking
 */
contract CarbonCreditToken is ERC20, ERC20Burnable, ERC20Pausable, Ownable, ReentrancyGuard {

    uint256 private _batchIdCounter;

    struct CreditBatch {
        uint256 projectId;
        uint256 amount;
        uint256 vintage; // Year of credit generation
        string verificationStandard;
        string ipfsMetadata;
        bool isRetired;
        uint256 createdAt;
        address originalOwner;
    }

    struct RetirementInfo {
        uint256 batchId;
        uint256 amount;
        string reason;
        address retiredBy;
        uint256 timestamp;
    }

    mapping(uint256 => CreditBatch) public creditBatches; // batchId => CreditBatch
    mapping(address => uint256[]) public ownerBatches; // owner => batchIds[]
    mapping(uint256 => uint256[]) public projectBatches; // projectId => batchIds[]
    mapping(uint256 => RetirementInfo[]) public batchRetirements; // batchId => RetirementInfo[]
    mapping(address => uint256) public totalRetiredCredits; // address => total retired amount

    uint256 public totalIssuedCredits;
    uint256 public totalRetiredCreditsGlobal;

    event CreditBatchMinted(
        uint256 indexed batchId,
        uint256 indexed projectId,
        uint256 amount,
        uint256 vintage,
        address indexed owner
    );

    event CreditsRetired(
        uint256 indexed batchId,
        uint256 amount,
        address indexed retiredBy,
        string reason
    );

    event BatchMetadataUpdated(
        uint256 indexed batchId,
        string newIpfsMetadata
    );

    modifier validBatchId(uint256 _batchId) {
        require(_batchId > 0 && _batchId <= _batchIdCounter, "Invalid batch ID");
        _;
    }

    modifier onlyBatchOwner(uint256 _batchId) {
        require(ownerOf(_batchId) == msg.sender, "Not batch owner");
        _;
    }

    constructor(
        string memory _name,
        string memory _symbol
    ) ERC20(_name, _symbol) {
        // Initialize with 18 decimal places (standard for carbon credits)
    }

    /**
     * @dev Mint a new batch of carbon credits
     */
    function mintCreditBatch(
        uint256 _projectId,
        uint256 _amount,
        uint256 _vintage,
        string memory _verificationStandard,
        string memory _ipfsMetadata,
        address _to
    ) external onlyOwner returns (uint256) {
        require(_projectId > 0, "Invalid project ID");
        require(_amount > 0, "Amount must be greater than 0");
        require(_vintage > 0, "Invalid vintage year");
        require(_to != address(0), "Invalid recipient address");
        require(bytes(_verificationStandard).length > 0, "Verification standard required");

        _batchIdCounter++;
        uint256 newBatchId = _batchIdCounter;

        // Create batch record
        CreditBatch storage newBatch = creditBatches[newBatchId];
        newBatch.projectId = _projectId;
        newBatch.amount = _amount;
        newBatch.vintage = _vintage;
        newBatch.verificationStandard = _verificationStandard;
        newBatch.ipfsMetadata = _ipfsMetadata;
        newBatch.isRetired = false;
        newBatch.createdAt = block.timestamp;
        newBatch.originalOwner = _to;

        // Update tracking mappings
        ownerBatches[_to].push(newBatchId);
        projectBatches[_projectId].push(newBatchId);

        // Mint ERC20 tokens
        _mint(_to, _amount * 10**decimals());

        // Update global counters
        totalIssuedCredits += _amount;

        emit CreditBatchMinted(newBatchId, _projectId, _amount, _vintage, _to);

        return newBatchId;
    }

    /**
     * @dev Retire carbon credits permanently
     */
    function retireCredits(
        uint256 _batchId,
        uint256 _amount,
        string memory _reason
    ) external validBatchId(_batchId) nonReentrant {
        require(_amount > 0, "Amount must be greater than 0");
        require(bytes(_reason).length > 0, "Retirement reason required");

        CreditBatch storage batch = creditBatches[_batchId];
        require(!batch.isRetired, "Batch already retired");

        uint256 tokenAmount = _amount * 10**decimals();
        require(balanceOf(msg.sender) >= tokenAmount, "Insufficient balance");

        // Burn the tokens
        _burn(msg.sender, tokenAmount);

        // Record retirement
        RetirementInfo memory retirement = RetirementInfo({
            batchId: _batchId,
            amount: _amount,
            reason: _reason,
            retiredBy: msg.sender,
            timestamp: block.timestamp
        });

        batchRetirements[_batchId].push(retirement);
        totalRetiredCredits[msg.sender] += _amount;
        totalRetiredCreditsGlobal += _amount;

        // Mark batch as retired if fully retired
        if (getTotalRetiredFromBatch(_batchId) >= batch.amount) {
            batch.isRetired = true;
        }

        emit CreditsRetired(_batchId, _amount, msg.sender, _reason);
    }

    /**
     * @dev Update batch metadata (IPFS hash)
     */
    function updateBatchMetadata(
        uint256 _batchId,
        string memory _newIpfsMetadata
    ) external onlyOwner validBatchId(_batchId) {
        require(bytes(_newIpfsMetadata).length > 0, "IPFS metadata cannot be empty");

        creditBatches[_batchId].ipfsMetadata = _newIpfsMetadata;

        emit BatchMetadataUpdated(_batchId, _newIpfsMetadata);
    }

    /**
     * @dev Get batch information
     */
    function getBatch(uint256 _batchId) external view validBatchId(_batchId) returns (CreditBatch memory) {
        return creditBatches[_batchId];
    }

    /**
     * @dev Get batches owned by an address
     */
    function getOwnerBatches(address _owner) external view returns (uint256[] memory) {
        return ownerBatches[_owner];
    }

    /**
     * @dev Get batches for a project
     */
    function getProjectBatches(uint256 _projectId) external view returns (uint256[] memory) {
        return projectBatches[_projectId];
    }

    /**
     * @dev Get retirement history for a batch
     */
    function getBatchRetirements(uint256 _batchId) external view validBatchId(_batchId) returns (RetirementInfo[] memory) {
        return batchRetirements[_batchId];
    }

    /**
     * @dev Get total retired credits from a specific batch
     */
    function getTotalRetiredFromBatch(uint256 _batchId) public view validBatchId(_batchId) returns (uint256) {
        RetirementInfo[] memory retirements = batchRetirements[_batchId];
        uint256 total = 0;

        for (uint256 i = 0; i < retirements.length; i++) {
            total += retirements[i].amount;
        }

        return total;
    }

    /**
     * @dev Get the owner of a batch (original minter)
     */
    function ownerOf(uint256 _batchId) public view validBatchId(_batchId) returns (address) {
        return creditBatches[_batchId].originalOwner;
    }

    /**
     * @dev Get total number of batches
     */
    function getTotalBatches() external view returns (uint256) {
        return _batchIdCounter;
    }

    /**
     * @dev Get global credit statistics
     */
    function getGlobalStats() external view returns (
        uint256 issued,
        uint256 retired,
        uint256 circulating
    ) {
        return (
            totalIssuedCredits,
            totalRetiredCreditsGlobal,
            totalIssuedCredits - totalRetiredCreditsGlobal
        );
    }

    /**
     * @dev Check if a batch exists
     */
    function batchExists(uint256 _batchId) external view returns (bool) {
        return _batchId > 0 && _batchId <= _batchIdCounter;
    }

    /**
     * @dev Pause token transfers (emergency function)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause token transfers
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    // Override required by Solidity for multiple inheritance
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override(ERC20, ERC20Pausable) {
        super._beforeTokenTransfer(from, to, amount);
    }
}

contracts/PaymentDistributor.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./CarbonCreditToken.sol";

/**
 * @title PaymentDistributor
 * @dev Handles automated payment distribution when carbon credits are sold
 */
contract PaymentDistributor is Ownable, ReentrancyGuard {

    struct PaymentSplit {
        address[] communityWallets;
        uint256[] communityPercentages; // Basis points (100 = 1%)
        address[] ngoWallets;
        uint256[] ngoPercentages;
        address[] verifierWallets;
        uint256[] verifierPercentages;
        address platformWallet;
        uint256 platformPercentage;
    }

    struct Sale {
        uint256 projectId;
        uint256 batchId;
        uint256 amount;
        uint256 price;
        address buyer;
        address seller;
        uint256 timestamp;
        bool isDistributed;
    }

    mapping(uint256 => PaymentSplit) public projectPaymentSplits; // projectId => PaymentSplit
    mapping(uint256 => Sale) public sales; // saleId => Sale
    mapping(address => uint256) public pendingWithdrawals;

    CarbonCreditToken public carbonCreditToken;
    address public platformTreasury;
    uint256 public defaultPlatformFee = 250; // 2.5% in basis points
    uint256 private _saleIdCounter;

    event PaymentSplitConfigured(
        uint256 indexed projectId,
        address[] communityWallets,
        uint256[] communityPercentages
    );

    event SaleRecorded(
        uint256 indexed saleId,
        uint256 indexed projectId,
        uint256 indexed batchId,
        uint256 amount,
        uint256 price,
        address buyer,
        address seller
    );

    event PaymentDistributed(
        uint256 indexed saleId,
        uint256 indexed projectId,
        uint256 totalAmount,
        uint256 timestamp
    );

    event FundsWithdrawn(
        address indexed recipient,
        uint256 amount
    );

    modifier validProjectId(uint256 _projectId) {
        require(_projectId > 0, "Invalid project ID");
        _;
    }

    constructor(
        address _carbonCreditToken,
        address _platformTreasury
    ) {
        require(_carbonCreditToken != address(0), "Invalid token address");
        require(_platformTreasury != address(0), "Invalid treasury address");

        carbonCreditToken = CarbonCreditToken(_carbonCreditToken);
        platformTreasury = _platformTreasury;
    }

    /**
     * @dev Configure payment split for a project
     */
    function configurePaymentSplit(
        uint256 _projectId,
        address[] memory _communityWallets,
        uint256[] memory _communityPercentages,
        address[] memory _ngoWallets,
        uint256[] memory _ngoPercentages,
        address[] memory _verifierWallets,
        uint256[] memory _verifierPercentages,
        address _platformWallet,
        uint256 _platformPercentage
    ) external onlyOwner validProjectId(_projectId) {
        require(_communityWallets.length == _communityPercentages.length, "Community arrays length mismatch");
        require(_ngoWallets.length == _ngoPercentages.length, "NGO arrays length mismatch");
        require(_verifierWallets.length == _verifierPercentages.length, "Verifier arrays length mismatch");

        // Validate total percentages don't exceed 100%
        uint256 totalPercentage = _platformPercentage;

        for (uint256 i = 0; i < _communityPercentages.length; i++) {
            totalPercentage += _communityPercentages[i];
        }
        for (uint256 i = 0; i < _ngoPercentages.length; i++) {
            totalPercentage += _ngoPercentages[i];
        }
        for (uint256 i = 0; i < _verifierPercentages.length; i++) {
            totalPercentage += _verifierPercentages[i];
        }

        require(totalPercentage <= 10000, "Total percentage exceeds 100%");

        PaymentSplit storage split = projectPaymentSplits[_projectId];
        split.communityWallets = _communityWallets;
        split.communityPercentages = _communityPercentages;
        split.ngoWallets = _ngoWallets;
        split.ngoPercentages = _ngoPercentages;
        split.verifierWallets = _verifierWallets;
        split.verifierPercentages = _verifierPercentages;
        split.platformWallet = _platformWallet != address(0) ? _platformWallet : platformTreasury;
        split.platformPercentage = _platformPercentage > 0 ? _platformPercentage : defaultPlatformFee;

        emit PaymentSplitConfigured(_projectId, _communityWallets, _communityPercentages);
    }

    /**
     * @dev Record a carbon credit sale and distribute payments
     */
    function recordSaleAndDistribute(
        uint256 _projectId,
        uint256 _batchId,
        uint256 _amount,
        uint256 _price,
        address _buyer,
        address _seller
    ) external payable nonReentrant validProjectId(_projectId) {
        require(_amount > 0, "Amount must be greater than 0");
        require(_price > 0, "Price must be greater than 0");
        require(_buyer != address(0), "Invalid buyer address");
        require(_seller != address(0), "Invalid seller address");
        require(msg.value >= _price, "Insufficient payment");

        _saleIdCounter++;
        uint256 saleId = _saleIdCounter;

        Sale storage sale = sales[saleId];
        sale.projectId = _projectId;
        sale.batchId = _batchId;
        sale.amount = _amount;
        sale.price = _price;
        sale.buyer = _buyer;
        sale.seller = _seller;
        sale.timestamp = block.timestamp;
        sale.isDistributed = false;

        emit SaleRecorded(saleId, _projectId, _batchId, _amount, _price, _buyer, _seller);

        // Distribute payments
        _distributePayment(saleId, _projectId, _price);

        // Refund excess payment
        if (msg.value > _price) {
            payable(msg.sender).transfer(msg.value - _price);
        }
    }

    /**
     * @dev Internal function to distribute payment according to configured splits
     */
    function _distributePayment(
        uint256 _saleId,
        uint256 _projectId,
        uint256 _totalAmount
    ) internal {
        PaymentSplit storage split = projectPaymentSplits[_projectId];

        // Distribute to community wallets
        for (uint256 i = 0; i < split.communityWallets.length; i++) {
            uint256 amount = (_totalAmount * split.communityPercentages[i]) / 10000;
            if (amount > 0) {
                pendingWithdrawals[split.communityWallets[i]] += amount;
            }
        }

        // Distribute to NGO wallets
        for (uint256 i = 0; i < split.ngoWallets.length; i++) {
            uint256 amount = (_totalAmount * split.ngoPercentages[i]) / 10000;
            if (amount > 0) {
                pendingWithdrawals[split.ngoWallets[i]] += amount;
            }
        }

        // Distribute to verifier wallets
        for (uint256 i = 0; i < split.verifierWallets.length; i++) {
            uint256 amount = (_totalAmount * split.verifierPercentages[i]) / 10000;
            if (amount > 0) {
                pendingWithdrawals[split.verifierWallets[i]] += amount;
            }
        }

        // Platform fee
        uint256 platformAmount = (_totalAmount * split.platformPercentage) / 10000;
        if (platformAmount > 0) {
            pendingWithdrawals[split.platformWallet] += platformAmount;
        }

        sales[_saleId].isDistributed = true;

        emit PaymentDistributed(_saleId, _projectId, _totalAmount, block.timestamp);
    }

    /**
     * @dev Withdraw pending payments
     */
    function withdraw() external nonReentrant {
        uint256 amount = pendingWithdrawals[msg.sender];
        require(amount > 0, "No funds to withdraw");

        pendingWithdrawals[msg.sender] = 0;

        (bool success, ) = payable(msg.sender).call{value: amount}("");
        require(success, "Transfer failed");

        emit FundsWithdrawn(msg.sender, amount);
    }

    /**
     * @dev Emergency withdraw for owner
     */
    function emergencyWithdraw(address _recipient, uint256 _amount) external onlyOwner {
        require(_recipient != address(0), "Invalid recipient");
        require(_amount <= address(this).balance, "Insufficient balance");

        (bool success, ) = payable(_recipient).call{value: _amount}("");
        require(success, "Transfer failed");
    }

    /**
     * @dev Update platform treasury address
     */
    function updatePlatformTreasury(address _newTreasury) external onlyOwner {
        require(_newTreasury != address(0), "Invalid treasury address");
        platformTreasury = _newTreasury;
    }

    /**
     * @dev Update default platform fee
     */
    function updateDefaultPlatformFee(uint256 _newFee) external onlyOwner {
        require(_newFee <= 1000, "Fee cannot exceed 10%"); // Max 10%
        defaultPlatformFee = _newFee;
    }

    /**
     * @dev Get payment split configuration for a project
     */
    function getPaymentSplit(uint256 _projectId) external view returns (PaymentSplit memory) {
        return projectPaymentSplits[_projectId];
    }

    /**
     * @dev Get sale details
     */
    function getSale(uint256 _saleId) external view returns (Sale memory) {
        return sales[_saleId];
    }

    /**
     * @dev Get pending withdrawal amount for an address
     */
    function getPendingWithdrawal(address _address) external view returns (uint256) {
        return pendingWithdrawals[_address];
    }

    /**
     * @dev Get total sales count
     */
    function getTotalSales() external view returns (uint256) {
        return _saleIdCounter;
    }

    // Function to receive Ether
    receive() external payable {}
}

contracts/ProjectRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

/**
 * @title ProjectRegistry
 * @dev Registry contract for blue carbon restoration projects
 */
contract ProjectRegistry is Ownable, ReentrancyGuard {

    uint256 private _projectIdCounter;

    enum ProjectStatus {
        Pending,
        Approved,
        Active,
        Completed,
        Suspended
    }

    enum EcosystemType {
        Mangrove,
        Seagrass,
        SaltMarsh
    }

    struct Project {
        uint256 id;
        string name;
        string description;
        EcosystemType ecosystemType;
        string location; // GPS coordinates and address
        uint256 areaInHectares;
        address projectOwner;
        address[] communityWallets;
        address[] verifiers;
        ProjectStatus status;
        uint256 createdAt;
        uint256 approvedAt;
        string ipfsHashMetadata; // IPFS hash for detailed project data
        uint256 estimatedCarbonCredits;
        uint256 totalCarbonCredits;
        bool isActive;
    }

    struct ProjectMetadata {
        string[] ipfsPhotos; // IPFS hashes of project photos
        string[] ipfsDroneData; // IPFS hashes of drone imagery
        string[] ipfsSatelliteData; // IPFS hashes of satellite data
        uint256 lastUpdated;
        string additionalNotes;
    }

    mapping(uint256 => Project) public projects;
    mapping(uint256 => ProjectMetadata) public projectMetadata;
    mapping(address => uint256[]) public ownerProjects;
    mapping(address => bool) public authorizedVerifiers;

    event ProjectRegistered(
        uint256 indexed projectId,
        address indexed owner,
        string name,
        EcosystemType ecosystemType,
        string location
    );

    event ProjectApproved(
        uint256 indexed projectId,
        address indexed approver,
        uint256 approvedAt
    );

    event ProjectStatusUpdated(
        uint256 indexed projectId,
        ProjectStatus oldStatus,
        ProjectStatus newStatus
    );

    event MetadataUpdated(
        uint256 indexed projectId,
        string ipfsHash,
        uint256 timestamp
    );

    event VerifierAdded(address indexed verifier);
    event VerifierRemoved(address indexed verifier);

    modifier onlyAuthorizedVerifier() {
        require(authorizedVerifiers[msg.sender] || msg.sender == owner(), "Not authorized verifier");
        _;
    }

    modifier projectExists(uint256 _projectId) {
        require(_projectId > 0 && _projectId <= _projectIdCounter, "Project does not exist");
        _;
    }

    constructor() {}

    /**
     * @dev Register a new blue carbon restoration project
     */
    function registerProject(
        string memory _name,
        string memory _description,
        EcosystemType _ecosystemType,
        string memory _location,
        uint256 _areaInHectares,
        address[] memory _communityWallets,
        string memory _ipfsHashMetadata,
        uint256 _estimatedCarbonCredits
    ) external returns (uint256) {
        require(bytes(_name).length > 0, "Project name cannot be empty");
        require(bytes(_location).length > 0, "Location cannot be empty");
        require(_areaInHectares > 0, "Area must be greater than 0");
        require(_estimatedCarbonCredits > 0, "Estimated credits must be greater than 0");

        _projectIdCounter++;
        uint256 newProjectId = _projectIdCounter;

        Project storage newProject = projects[newProjectId];
        newProject.id = newProjectId;
        newProject.name = _name;
        newProject.description = _description;
        newProject.ecosystemType = _ecosystemType;
        newProject.location = _location;
        newProject.areaInHectares = _areaInHectares;
        newProject.projectOwner = msg.sender;
        newProject.communityWallets = _communityWallets;
        newProject.status = ProjectStatus.Pending;
        newProject.createdAt = block.timestamp;
        newProject.ipfsHashMetadata = _ipfsHashMetadata;
        newProject.estimatedCarbonCredits = _estimatedCarbonCredits;
        newProject.isActive = true;

        ownerProjects[msg.sender].push(newProjectId);

        emit ProjectRegistered(
            newProjectId,
            msg.sender,
            _name,
            _ecosystemType,
            _location
        );

        return newProjectId;
    }

    /**
     * @dev Approve a project (only authorized verifiers)
     */
    function approveProject(uint256 _projectId) 
        external 
        onlyAuthorizedVerifier 
        projectExists(_projectId) 
    {
        Project storage project = projects[_projectId];
        require(project.status == ProjectStatus.Pending, "Project not in pending status");

        ProjectStatus oldStatus = project.status;
        project.status = ProjectStatus.Approved;
        project.approvedAt = block.timestamp;

        emit ProjectApproved(_projectId, msg.sender, block.timestamp);
        emit ProjectStatusUpdated(_projectId, oldStatus, ProjectStatus.Approved);
    }

    /**
     * @dev Update project status
     */
    function updateProjectStatus(uint256 _projectId, ProjectStatus _newStatus)
        external
        onlyAuthorizedVerifier
        projectExists(_projectId)
    {
        Project storage project = projects[_projectId];
        ProjectStatus oldStatus = project.status;
        project.status = _newStatus;

        emit ProjectStatusUpdated(_projectId, oldStatus, _newStatus);
    }

    /**
     * @dev Update project metadata with new IPFS data
     */
    function updateProjectMetadata(
        uint256 _projectId,
        string[] memory _ipfsPhotos,
        string[] memory _ipfsDroneData,
        string[] memory _ipfsSatelliteData,
        string memory _additionalNotes
    ) external projectExists(_projectId) {
        Project storage project = projects[_projectId];
        require(
            msg.sender == project.projectOwner || 
            authorizedVerifiers[msg.sender] || 
            msg.sender == owner(),
            "Not authorized to update metadata"
        );

        ProjectMetadata storage metadata = projectMetadata[_projectId];
        metadata.ipfsPhotos = _ipfsPhotos;
        metadata.ipfsDroneData = _ipfsDroneData;
        metadata.ipfsSatelliteData = _ipfsSatelliteData;
        metadata.lastUpdated = block.timestamp;
        metadata.additionalNotes = _additionalNotes;

        emit MetadataUpdated(_projectId, project.ipfsHashMetadata, block.timestamp);
    }

    /**
     * @dev Add authorized verifier
     */
    function addVerifier(address _verifier) external onlyOwner {
        require(_verifier != address(0), "Invalid verifier address");
        authorizedVerifiers[_verifier] = true;

        emit VerifierAdded(_verifier);
    }

    /**
     * @dev Remove authorized verifier
     */
    function removeVerifier(address _verifier) external onlyOwner {
        authorizedVerifiers[_verifier] = false;

        emit VerifierRemoved(_verifier);
    }

    /**
     * @dev Update total carbon credits for a project
     */
    function updateTotalCarbonCredits(uint256 _projectId, uint256 _credits)
        external
        onlyAuthorizedVerifier
        projectExists(_projectId)
    {
        projects[_projectId].totalCarbonCredits = _credits;
    }

    /**
     * @dev Get project details
     */
    function getProject(uint256 _projectId) 
        external 
        view 
        projectExists(_projectId) 
        returns (Project memory) 
    {
        return projects[_projectId];
    }

    /**
     * @dev Get project metadata
     */
    function getProjectMetadata(uint256 _projectId)
        external
        view
        projectExists(_projectId)
        returns (ProjectMetadata memory)
    {
        return projectMetadata[_projectId];
    }

    /**
     * @dev Get projects by owner
     */
    function getProjectsByOwner(address _owner) external view returns (uint256[] memory) {
        return ownerProjects[_owner];
    }

    /**
     * @dev Get total number of projects
     */
    function getTotalProjects() external view returns (uint256) {
        return _projectIdCounter;
    }

    /**
     * @dev Check if address is authorized verifier
     */
    function isAuthorizedVerifier(address _verifier) external view returns (bool) {
        return authorizedVerifiers[_verifier];
    }
}

contracts/UniqueCarbonCreditNFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

/**
 * @title UniqueCarbonCreditNFT
 * @dev ERC1155 contract for unique, project-specific carbon credit NFTs
 */
contract UniqueCarbonCreditNFT is ERC1155, Ownable, ReentrancyGuard {

    struct UniqueCredit {
        uint256 projectId;
        uint256 creditAmount;
        string ipfsMetadata;
        string certificateName;
        bool isRetired;
        uint256 mintedAt;
        address originalMinter;
    }

    mapping(uint256 => UniqueCredit) public uniqueCredits; // tokenId => UniqueCredit
    mapping(uint256 => uint256[]) public projectTokens; // projectId => tokenIds[]
    mapping(address => uint256[]) public ownerTokens; // owner => tokenIds[]

    uint256 private _tokenIdCounter;

    event UniqueCreditMinted(
        uint256 indexed tokenId,
        uint256 indexed projectId,
        uint256 creditAmount,
        address indexed recipient,
        string certificateName
    );

    event UniqueCreditRetired(
        uint256 indexed tokenId,
        address indexed retiredBy,
        uint256 timestamp
    );

    constructor(string memory _uri) ERC1155(_uri) {
        // URI should be something like: "https://api.bluecarbon.com/metadata/{id}.json"
    }

    /**
     * @dev Mint a unique carbon credit NFT
     */
    function mintUniqueCredit(
        address _to,
        uint256 _tokenId,
        uint256 _creditAmount,
        string memory _ipfsMetadata,
        string memory _certificateName
    ) external onlyOwner {
        require(_to != address(0), "Invalid recipient address");
        require(_creditAmount > 0, "Credit amount must be greater than 0");
        require(bytes(_certificateName).length > 0, "Certificate name required");
        require(uniqueCredits[_tokenId].mintedAt == 0, "Token ID already exists");

        // Create unique credit record
        UniqueCredit storage newCredit = uniqueCredits[_tokenId];
        newCredit.projectId = 0; // Will be set by project owner
        newCredit.creditAmount = _creditAmount;
        newCredit.ipfsMetadata = _ipfsMetadata;
        newCredit.certificateName = _certificateName;
        newCredit.isRetired = false;
        newCredit.mintedAt = block.timestamp;
        newCredit.originalMinter = _to;

        // Update tracking mappings
        ownerTokens[_to].push(_tokenId);

        // Mint the NFT
        _mint(_to, _tokenId, 1, "");

        emit UniqueCreditMinted(_tokenId, 0, _creditAmount, _to, _certificateName);
    }

    /**
     * @dev Mint a project-specific unique carbon credit NFT
     */
    function mintProjectCredit(
        address _to,
        uint256 _projectId,
        uint256 _creditAmount,
        string memory _ipfsMetadata,
        string memory _certificateName
    ) external onlyOwner returns (uint256) {
        require(_to != address(0), "Invalid recipient address");
        require(_projectId > 0, "Invalid project ID");
        require(_creditAmount > 0, "Credit amount must be greater than 0");
        require(bytes(_certificateName).length > 0, "Certificate name required");

        _tokenIdCounter++;
        uint256 newTokenId = _tokenIdCounter;

        // Create unique credit record
        UniqueCredit storage newCredit = uniqueCredits[newTokenId];
        newCredit.projectId = _projectId;
        newCredit.creditAmount = _creditAmount;
        newCredit.ipfsMetadata = _ipfsMetadata;
        newCredit.certificateName = _certificateName;
        newCredit.isRetired = false;
        newCredit.mintedAt = block.timestamp;
        newCredit.originalMinter = _to;

        // Update tracking mappings
        projectTokens[_projectId].push(newTokenId);
        ownerTokens[_to].push(newTokenId);

        // Mint the NFT
        _mint(_to, newTokenId, 1, "");

        emit UniqueCreditMinted(newTokenId, _projectId, _creditAmount, _to, _certificateName);

        return newTokenId;
    }

    /**
     * @dev Retire a unique carbon credit NFT
     */
    function retireUniqueCredit(uint256 _tokenId) external nonReentrant {
        require(balanceOf(msg.sender, _tokenId) > 0, "Not the owner of this NFT");
        require(!uniqueCredits[_tokenId].isRetired, "Credit already retired");

        // Mark as retired
        uniqueCredits[_tokenId].isRetired = true;

        // Burn the NFT
        _burn(msg.sender, _tokenId, 1);

        emit UniqueCreditRetired(_tokenId, msg.sender, block.timestamp);
    }

    /**
     * @dev Update NFT metadata
     */
    function updateTokenMetadata(
        uint256 _tokenId,
        string memory _newIpfsMetadata
    ) external onlyOwner {
        require(uniqueCredits[_tokenId].mintedAt > 0, "Token does not exist");
        require(bytes(_newIpfsMetadata).length > 0, "IPFS metadata cannot be empty");

        uniqueCredits[_tokenId].ipfsMetadata = _newIpfsMetadata;
    }

    /**
     * @dev Get unique credit details
     */
    function getUniqueCredit(uint256 _tokenId) external view returns (UniqueCredit memory) {
        require(uniqueCredits[_tokenId].mintedAt > 0, "Token does not exist");
        return uniqueCredits[_tokenId];
    }

    /**
     * @dev Get all token IDs for a project
     */
    function getProjectTokens(uint256 _projectId) external view returns (uint256[] memory) {
        return projectTokens[_projectId];
    }

    /**
     * @dev Get all token IDs owned by an address
     */
    function getOwnerTokens(address _owner) external view returns (uint256[] memory) {
        return ownerTokens[_owner];
    }

    /**
     * @dev Get total number of unique credits minted
     */
    function getTotalSupply() external view returns (uint256) {
        return _tokenIdCounter;
    }

    /**
     * @dev Check if a token is retired
     */
    function isRetired(uint256 _tokenId) external view returns (bool) {
        return uniqueCredits[_tokenId].isRetired;
    }

    /**
     * @dev Set new URI for metadata
     */
    function setURI(string memory _newURI) external onlyOwner {
        _setURI(_newURI);
    }

    /**
     * @dev Override transfer functions to update owner tracking
     */
    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal override {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);

        // Update owner tracking when transferring
        if (from != address(0) && to != address(0) && from != to) {
            for (uint256 i = 0; i < ids.length; i++) {
                uint256 tokenId = ids[i];

                // Remove from old owner's list
                _removeTokenFromOwner(from, tokenId);

                // Add to new owner's list
                ownerTokens[to].push(tokenId);
            }
        }
    }

    /**
     * @dev Remove token from owner's list
     */
    function _removeTokenFromOwner(address _owner, uint256 _tokenId) internal {
        uint256[] storage tokens = ownerTokens[_owner];
        for (uint256 i = 0; i < tokens.length; i++) {
            if (tokens[i] == _tokenId) {
                tokens[i] = tokens[tokens.length - 1];
                tokens.pop();
                break;
            }
        }
    }

    /**
     * @dev Support interface detection
     */
    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC1155) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}

contracts/VerificationOracle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./ProjectRegistry.sol";
import "./CarbonCreditToken.sol";

/**
 * @title VerificationOracle
 * @dev Handles multi-source verification of restoration projects
 */
contract VerificationOracle is Ownable, ReentrancyGuard {

    enum DataSource {
        Community,
        Satellite,
        Drone,
        ThirdParty
    }

    enum VerificationStatus {
        Pending,
        Verified,
        Rejected,
        RequiresReview
    }

    struct VerificationData {
        uint256 projectId;
        DataSource source;
        string ipfsHash;
        string description;
        address submitter;
        uint256 timestamp;
        VerificationStatus status;
        string notes;
        uint256 carbonCreditsEstimate;
    }

    struct ProjectVerification {
        uint256 projectId;
        uint256[] verificationIds;
        uint256 communityDataCount;
        uint256 satelliteDataCount;
        uint256 droneDataCount;
        uint256 thirdPartyDataCount;
        uint256 verifiedDataCount;
        bool isCompletelyVerified;
        uint256 finalCarbonCredits;
        uint256 lastUpdateTimestamp;
    }

    ProjectRegistry public projectRegistry;
    CarbonCreditToken public carbonCreditToken;

    mapping(uint256 => VerificationData) public verificationData; // verificationId => VerificationData
    mapping(uint256 => ProjectVerification) public projectVerifications; // projectId => ProjectVerification
    mapping(address => bool) public authorizedVerifiers;
    mapping(DataSource => uint256) public minimumDataRequirements; // Minimum data points needed per source
    mapping(DataSource => uint256) public verificationWeights; // Weight of each data source in final verification

    uint256 private _verificationIdCounter;
    uint256 public constant VERIFICATION_THRESHOLD = 7000; // 70% agreement needed (in basis points)

    event VerificationDataSubmitted(
        uint256 indexed verificationId,
        uint256 indexed projectId,
        DataSource source,
        address submitter,
        string ipfsHash
    );

    event VerificationStatusUpdated(
        uint256 indexed verificationId,
        VerificationStatus oldStatus,
        VerificationStatus newStatus,
        address updatedBy
    );

    event ProjectVerificationCompleted(
        uint256 indexed projectId,
        uint256 finalCarbonCredits,
        uint256 timestamp
    );

    event CarbonCreditsIssued(
        uint256 indexed projectId,
        uint256 amount,
        address recipient
    );

    modifier onlyAuthorizedVerifier() {
        require(authorizedVerifiers[msg.sender] || msg.sender == owner(), "Not authorized verifier");
        _;
    }

    modifier validProjectId(uint256 _projectId) {
        require(_projectId > 0, "Invalid project ID");
        _;
    }

    constructor(
        address _projectRegistry,
        address _carbonCreditToken
    ) {
        require(_projectRegistry != address(0), "Invalid project registry address");
        require(_carbonCreditToken != address(0), "Invalid carbon credit token address");

        projectRegistry = ProjectRegistry(_projectRegistry);
        carbonCreditToken = CarbonCreditToken(_carbonCreditToken);

        // Set default minimum data requirements
        minimumDataRequirements[DataSource.Community] = 3;
        minimumDataRequirements[DataSource.Satellite] = 2;
        minimumDataRequirements[DataSource.Drone] = 1;
        minimumDataRequirements[DataSource.ThirdParty] = 1;

        // Set default verification weights (basis points)
        verificationWeights[DataSource.Community] = 3000; // 30%
        verificationWeights[DataSource.Satellite] = 3500; // 35%
        verificationWeights[DataSource.Drone] = 2500; // 25%
        verificationWeights[DataSource.ThirdParty] = 1000; // 10%
    }

    /**
     * @dev Submit verification data for a project
     */
    function submitVerificationData(
        uint256 _projectId,
        DataSource _source,
        string memory _ipfsHash,
        string memory _description,
        uint256 _carbonCreditsEstimate
    ) external validProjectId(_projectId) returns (uint256) {
        require(bytes(_ipfsHash).length > 0, "IPFS hash cannot be empty");
        require(_carbonCreditsEstimate > 0, "Carbon credits estimate must be greater than 0");

        _verificationIdCounter++;
        uint256 verificationId = _verificationIdCounter;

        VerificationData storage newData = verificationData[verificationId];
        newData.projectId = _projectId;
        newData.source = _source;
        newData.ipfsHash = _ipfsHash;
        newData.description = _description;
        newData.submitter = msg.sender;
        newData.timestamp = block.timestamp;
        newData.status = VerificationStatus.Pending;
        newData.carbonCreditsEstimate = _carbonCreditsEstimate;

        // Update project verification tracking
        ProjectVerification storage projectVerif = projectVerifications[_projectId];
        if (projectVerif.projectId == 0) {
            projectVerif.projectId = _projectId;
        }

        projectVerif.verificationIds.push(verificationId);

        // Update data source counts
        if (_source == DataSource.Community) {
            projectVerif.communityDataCount++;
        } else if (_source == DataSource.Satellite) {
            projectVerif.satelliteDataCount++;
        } else if (_source == DataSource.Drone) {
            projectVerif.droneDataCount++;
        } else if (_source == DataSource.ThirdParty) {
            projectVerif.thirdPartyDataCount++;
        }

        projectVerif.lastUpdateTimestamp = block.timestamp;

        emit VerificationDataSubmitted(verificationId, _projectId, _source, msg.sender, _ipfsHash);

        return verificationId;
    }

    /**
     * @dev Update verification status (only authorized verifiers)
     */
    function updateVerificationStatus(
        uint256 _verificationId,
        VerificationStatus _newStatus,
        string memory _notes
    ) external onlyAuthorizedVerifier {
        require(_verificationId > 0 && _verificationId <= _verificationIdCounter, "Invalid verification ID");

        VerificationData storage data = verificationData[_verificationId];
        VerificationStatus oldStatus = data.status;
        data.status = _newStatus;
        data.notes = _notes;

        // Update verified data count
        ProjectVerification storage projectVerif = projectVerifications[data.projectId];

        if (oldStatus != VerificationStatus.Verified && _newStatus == VerificationStatus.Verified) {
            projectVerif.verifiedDataCount++;
        } else if (oldStatus == VerificationStatus.Verified && _newStatus != VerificationStatus.Verified) {
            projectVerif.verifiedDataCount--;
        }

        projectVerif.lastUpdateTimestamp = block.timestamp;

        emit VerificationStatusUpdated(_verificationId, oldStatus, _newStatus, msg.sender);

        // Check if project verification is complete
        _checkProjectVerificationCompletion(data.projectId);
    }

    /**
     * @dev Check if project verification is complete and issue credits if so
     */
    function _checkProjectVerificationCompletion(uint256 _projectId) internal {
        ProjectVerification storage projectVerif = projectVerifications[_projectId];

        // Check if minimum data requirements are met
        bool hasMinimumData = 
            projectVerif.communityDataCount >= minimumDataRequirements[DataSource.Community] &&
            projectVerif.satelliteDataCount >= minimumDataRequirements[DataSource.Satellite] &&
            projectVerif.droneDataCount >= minimumDataRequirements[DataSource.Drone] &&
            projectVerif.thirdPartyDataCount >= minimumDataRequirements[DataSource.ThirdParty];

        if (!hasMinimumData) {
            return;
        }

        // Calculate weighted verification score
        uint256 totalWeight = 0;
        uint256 verifiedWeight = 0;

        for (uint256 i = 0; i < projectVerif.verificationIds.length; i++) {
            uint256 verificationId = projectVerif.verificationIds[i];
            VerificationData storage data = verificationData[verificationId];

            uint256 weight = verificationWeights[data.source];
            totalWeight += weight;

            if (data.status == VerificationStatus.Verified) {
                verifiedWeight += weight;
            }
        }

        // Check if verification threshold is met
        uint256 verificationScore = (verifiedWeight * 10000) / totalWeight;

        if (verificationScore >= VERIFICATION_THRESHOLD && !projectVerif.isCompletelyVerified) {
            projectVerif.isCompletelyVerified = true;

            // Calculate final carbon credits based on verified data
            uint256 finalCredits = _calculateFinalCarbonCredits(_projectId);
            projectVerif.finalCarbonCredits = finalCredits;

            emit ProjectVerificationCompleted(_projectId, finalCredits, block.timestamp);

            // Update project registry
            projectRegistry.updateTotalCarbonCredits(_projectId, finalCredits);

            // Issue carbon credits
            _issueCarbonCredits(_projectId, finalCredits);
        }
    }

    /**
     * @dev Calculate final carbon credits based on verified data
     */
    function _calculateFinalCarbonCredits(uint256 _projectId) internal view returns (uint256) {
        ProjectVerification storage projectVerif = projectVerifications[_projectId];

        uint256 totalEstimate = 0;
        uint256 totalWeight = 0;

        for (uint256 i = 0; i < projectVerif.verificationIds.length; i++) {
            uint256 verificationId = projectVerif.verificationIds[i];
            VerificationData storage data = verificationData[verificationId];

            if (data.status == VerificationStatus.Verified) {
                uint256 weight = verificationWeights[data.source];
                totalEstimate += data.carbonCreditsEstimate * weight;
                totalWeight += weight;
            }
        }

        return totalWeight > 0 ? totalEstimate / totalWeight : 0;
    }

    /**
     * @dev Issue carbon credits to project owner
     */
    function _issueCarbonCredits(uint256 _projectId, uint256 _amount) internal {
        ProjectRegistry.Project memory project = projectRegistry.getProject(_projectId);

        // Mint carbon credits to project owner
        uint256 batchId = carbonCreditToken.mintCreditBatch(
            _projectId,
            _amount,
            block.timestamp, // vintage year
            "Blue Carbon MRV Standard",
            "", // IPFS metadata will be added separately
            project.projectOwner
        );

        emit CarbonCreditsIssued(_projectId, _amount, project.projectOwner);
    }

    /**
     * @dev Add authorized verifier
     */
    function addVerifier(address _verifier) external onlyOwner {
        require(_verifier != address(0), "Invalid verifier address");
        authorizedVerifiers[_verifier] = true;
    }

    /**
     * @dev Remove authorized verifier
     */
    function removeVerifier(address _verifier) external onlyOwner {
        authorizedVerifiers[_verifier] = false;
    }

    /**
     * @dev Update minimum data requirements
     */
    function updateMinimumDataRequirements(
        DataSource _source,
        uint256 _minimum
    ) external onlyOwner {
        minimumDataRequirements[_source] = _minimum;
    }

    /**
     * @dev Update verification weights
     */
    function updateVerificationWeights(
        DataSource _source,
        uint256 _weight
    ) external onlyOwner {
        require(_weight <= 10000, "Weight cannot exceed 100%");
        verificationWeights[_source] = _weight;
    }

    /**
     * @dev Get verification data
     */
    function getVerificationData(uint256 _verificationId) external view returns (VerificationData memory) {
        return verificationData[_verificationId];
    }

    /**
     * @dev Get project verification details
     */
    function getProjectVerification(uint256 _projectId) external view returns (ProjectVerification memory) {
        return projectVerifications[_projectId];
    }

    /**
     * @dev Get all verification IDs for a project
     */
    function getProjectVerificationIds(uint256 _projectId) external view returns (uint256[] memory) {
        return projectVerifications[_projectId].verificationIds;
    }

    /**
     * @dev Check if address is authorized verifier
     */
    function isAuthorizedVerifier(address _verifier) external view returns (bool) {
        return authorizedVerifiers[_verifier];
    }

    /**
     * @dev Get total verification count
     */
    function getTotalVerifications() external view returns (uint256) {
        return _verificationIdCounter;
    }

    /**
     * @dev Check if project meets verification requirements
     */
    function checkVerificationRequirements(uint256 _projectId) external view returns (bool) {
        ProjectVerification storage projectVerif = projectVerifications[_projectId];

        return projectVerif.communityDataCount >= minimumDataRequirements[DataSource.Community] &&
               projectVerif.satelliteDataCount >= minimumDataRequirements[DataSource.Satellite] &&
               projectVerif.droneDataCount >= minimumDataRequirements[DataSource.Drone] &&
               projectVerif.thirdPartyDataCount >= minimumDataRequirements[DataSource.ThirdParty];
    }
}

